import React from 'react'
import Button from 'react-bootstrap/Button'
class Details extends React.Component {

   render(){

      return(
          <div>
             <h4>school_name: {this.props.schools.school_name}</h4>
             <h4>description: {this.props.school.description}</h4>
             <h4>Rating: {this.props.school.rating}</h4>
             <Button onClick = {() => this.props.addToFavorites(this.props.school)} variant="outline-success">Add To Favorites</Button>
          </div>
      )
   }

}

export default Details
