import React from 'react'
import './signUp.css'
export default class SignUp extends React.Component {

    state = {
        first_name: '',
        last_name: '',
        password: '',
        zip_code: '',
        current_grade: ''

    }

    handleSubmit = () => {
        fetch('http://localhost:3000/signup',{
            method: 'POST',
            headers:{
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                first_name: this.state.first_name,
                last_name: this.state.last_name,
                password: this.state.password,
                zip_code: this.state.zip_code,
                current_grade: this.state.current_grade

            })
            })

    }


    handleChange = (e) => {
      this.setState({
            [e.target.name]: e.target.value
      })

    }



    render(){
        return(

        <div className="login-page">
            <h1 > <strong> Welcome to school locator </strong> </h1>

                <div className="form">
                <form onSubmit={this.handleSubmit} className="login-form">
                  <input name = "first_name"  type="name" placeholder="first_name" onChange={this.handleChange}/>
                  <input name = "last_name"    type = "name" placeholder = "password" onChange={this.handleChange}/>
                  <input name = "password"  type="name" placeholder="first_name" onChange={this.handleChange}/>
                  <input name = "zip"    type = "name" placeholder = "password" onChange={this.handleChange}/>



                  <button style={{"border-radius": "7px"}}>Sign Up</button>

                    <p className="message">Already registered? <a href="">Sign In</a></p>
                </form>
                </div>
                <h2> <strong> </strong> </h2>
              </div>
        )
    }
}
