import React from 'react'
import Row from "react-bootstrap/Row"
import FavoriteCards from './favoriteCards'

 class Favorites extends React.Component {

     state = {
         favorites: [],
         showCards: true,
         showForm:false

     }

    componentDidMount() {
        fetch(`http://localhost:3000/favoriteSchools/${localStorage.getItem('user')}`, {
            method: "GET",
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(res => res.json())
         .then(data =>{ 
            console.log('this is data', data)
            this.setState({
                favorites: data
            })
        })
    }

    handleShowCard = () =>{
         this.setState({
             cardsShow:!this.state.cardsShow
         })

    }


    handleApply = (school) =>{
         fetch(`http://localhost:3000/favoriteSchools/${localStorage.getItem('user')}/${school.school_id}`,{
             method: "PATCH",
             headers:{
                 'Content-Type': 'application/json',
                 'Authorization': `Bearer ${localStorage.getItem('token')}`
             },
             body: JSON.stringify({
                 has_applied: true
             })
         })
         .then(r => r.json())
         .then( fav => {
             this.setState({
                 favorites: this.state.favorites.map( eachFav => eachFav.id === fav.id ? fav : eachFav )
             })
         })


    }

    remove = (school) => {

            fetch(`http://localhost:3000/favoriteSchools/${localStorage.getItem('user')}/${school.school_id}`,{
                method: "DELETE",
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}` 
                }
            })
             console.log('remove school',school.school_id)
            let mySchools = this.state.favorites.filter(schools => {
                return schools.id !== school.id
            })
            this.setState({
                favorites: mySchools
            })
    }


    sendToPage = () => {
         this.props.history.push('/applicationSubmit')
    }

     render (){

         let allFavorites = this.state.favorites
         console.log(allFavorites.length)
         let cards;

         if (allFavorites.length > 0 ) {
            this.state.favorites.map(favorite =>{
            cards = <FavoriteCards  favorite = {favorite}   remove = {this.remove}  sendToPage={this.sendToPage} applay ={()=>this.handleApply(favorite)}/>
            })
         } else {
            cards = <a href ="./home" style={{fontSize: '50px',marginLeft: '50px'}}>Back</a>
         }

         return(
          <Row style = {{backgroundColor: '#a3a375',height: '100vh'}}>   
                    <div> 
                        {cards}

                    </div>
          </Row>
         )
     }
 }


 export default Favorites
