import React, {useState} from 'react'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import Col from 'react-bootstrap/Col'
import Modal from 'react-bootstrap/Modal'
import ModalHeader from 'react-bootstrap/ModalHeader'
import  ApplicationForm from './applicationForm'
import ApplicationSubmition from './applicationSubmition';

function  FavoriteCards () {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);



    handleView = () => {
        // this.setState({
        //     ShowSubmitionForm: !this.state.ShowSubmitionForm
        // })
        this.props.history.push('/applicationSubmition')

    }

    // sendToPage = () => {
    //      this.props.sendToPage()

    // }


        console.log(this.props.favorite)
        return (
          <div>
            <Col sm ={5}>
            <Card style={{ opacity: '.8',width: '18rem','border-radius': '7px', marginLeft: '16%', marginTop: '10%',height: '40vh',width: '40vh',fontStyle: 'bold'}}>
                    <Card.Body>
                        <Card.Title><h2>School Name: {this.props.favorite.school.school_name}</h2></Card.Title>
                        <Card.Text><h4>Description: {this.props.favorite.school.description}</h4></Card.Text>
                        <Card.Text><h4>Rating: {this.props.favorite.school.rating}</h4></Card.Text>
                        <Card.Text><h4>Grade: {this.props.favorite.school.grade}</h4></Card.Text>
                        <Button  variant="outline-primary" style = {{marginTop: '5px' }} onClick = {()=>this.props.applay(this.props.favorite)} size = 'lg' >Apply</Button>

                        {console.log(this.props.favorite)}
                        <br></br>
                        {this.props.favorite.has_applied ?
                        <div>
                        <Button variant = "outline-primary"  size ='lg' onClick = {this.sendToPage}>View Application</Button>
                        <br></br>
                        </div>
                        : null
                        }
                        <Button variant = "outline-danger" onClick = {() =>this.props.remove(this.props.favorite)} size = 'lg'>Remove </Button>
                        <br></br><br></br>
                        <a href = './home'><h3>Back</h3></a>
                    </Card.Body>
                </Card>
            </Col>
           </div> 

        )


}
export default FavoriteCards
