import React from 'react'
import { Map, GoogleApiWrapper, Marker,InfoWindow} from 'google-maps-react'
import Details from './details'


 class MapContainer extends React.Component {

    constructor (props){
        super(props)
        this.state = {
            schools: [{latitude: 29.774787, longitude: -95.380721},
                     {latitude: 29.763005, longitude: -95.282988}],
            showinginfoWindow: false,
            activeMarker: false,
            selectedPlace: {},
        }
    }

    // handleShow = () => {
    //     this.setState({
    //         showup:!this.state.showup
    //     })
    // }


    onMarkerClick = (props, e) => {
        console.log(props)
        this.setState({
        selectedPlace: props.school.school_name,
        activeMarker: !this.state.activeMarker,
        showinginfoWindow: !this.state.showinginfoWindow
        });
    }

    onClose = props => {
        if (this.state.showingInfoWindow) {
          this.setState({
            showingInfoWindow: false,
            activeMarker: null
          });
        }
      };



    componentDidMount(){
        fetch('http://localhost:3000/schools', {
            method: "GET",
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(res=>res.json())
          .then(data=>{
            this.setState({
                schools: data
            })
            console.log(data)
        })

    }


    displayMarkers = () => {
        return this.state.schools.map((school, index) => {
            return <Marker key={index} id={index} position={{
            lat: school.latitude,
            lng: school.longitude,
            }}
            school = {school}
            handleShow = {this.handleShow}
            onClick = { (e) => this.onMarkerClick(e)} school_name = 'hi'  />
        })
    }



    render() {
        return (
            <div>
                <Map 
                    google={this.props.google}
                    zoom={8}
                    style={{width: '100%', height: '100%', topmargin: '10px'}}
                    initialCenter={{ lat: 29.731589, lng: -95.310856}}
                >

                {/* <Marker
                onClick={this.onMarkerClick}
                name={'Kenyatta International Convention Centre'}
                /> */}

                <InfoWindow 
                marker={this.state.activeMarker}
                visible={this.state.showinginfoWindow}
                onClose={this.onClose}

                > 
                <div>
                    <h4>hii</h4>
                </div>
                </InfoWindow>



                {this.displayMarkers()}
                <h1>hi</h1>
                </Map>
                {/* {this.state.schools.map(school =>{
                    return(
                        <div>
                            <Details  school = {school} onClick = {this.handleShow}/>
                        </div>
                    )
                })} */}

            </div>
        )
    }
}

export default GoogleApiWrapper ({
    apiKey: 'AIzaSyCGkLGCv3BgUFVYOfNBk5oBmvH6ou-wU8w'
  })(MapContainer)
