import React from 'react'
class Details extends React.Component {

   render(){

      return(
          <div>
             <h3>{this.props.school.school_name}</h3>
             <h3>this.props.school.description</h3>
             <h3>this.props.school.rating</h3>
             <button onClick = {() => this.props.showDetails()}>Add To Favorites</button>
          </div>
      )
   }

}

export default Details
